import React, { Fragment, useEffect, useState } from "react";

const Categories = () => {
    const [category, setCategory] = useState([]);
    const [category_name, setCategoryName] = useState("");
    const [description, setDescription] = useState("");
    const [isEditing, setIsEditing] = useState(false);
    const [editId, setEditId] = useState(null);

    // Send or update the data
    const submitCategory = async (e) => {
        e.preventDefault();
        try {
            const body = { category_name, description };
            const url = isEditing
                ? `http://localhost:3000/category/${editId}`
                : "http://localhost:3000/category";
            const method = isEditing ? "PUT" : "POST";

            const response = await fetch(url, {
                method,
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body),
            });

            if (response.ok) {
                console.log(isEditing ? "Category updated successfully" : "Category added successfully");
                setCategoryName("");
                setDescription("");
                setIsEditing(false);
                setEditId(null);
                await allCategories();
            } else {
                console.log(isEditing ? "Failed to update category" : "Failed to add category");
            }
        } catch (err) {
            console.error(err.message);
        }
    };

    const handleCategoryName = (e) => {
        setCategoryName(e.target.value);
    };

    const handleCategoryDescription = (e) => {
        setDescription(e.target.value);
    };

    // Get all categories
    const allCategories = async () => {
        try {
            const response = await fetch("http://localhost:3000/category", {
                method: "GET",
            });
            const responseData = await response.json();
            setCategory(responseData);
        } catch (err) {
            console.error(err.message);
        }
    };

    // Delete a category
    const deleteCategory = async (id) => {
        try {
            const response = await fetch(`http://localhost:3000/category/${id}`, {
                method: "DELETE",
            });

            if (!response.ok) {
                throw new Error("Failed to delete category");
            }

            const responseData = await response.json();
            console.log(responseData);

            setCategory(category.filter((cate) => cate.category_id !== id));
        } catch (err) {
            console.error("Error:", err.message);
        }
    };

    // Open edit form with pre-filled data
    const openEditForm = (id) => {
        const categoryToEdit = category.find((cate) => cate.category_id === id);

        if (categoryToEdit) {
            setCategoryName(categoryToEdit.category_name);
            setDescription(categoryToEdit.description);
            setEditId(id);
            setIsEditing(true);
        }
    };

    useEffect(() => {
        allCategories();
    }, []);

    return (
        <Fragment>
            {/* Category Form */}
            <form onSubmit={submitCategory} className="p-6 bg-white rounded-lg shadow-md">
                <div className="mb-4">
                    <label htmlFor="category-name" className="block text-gray-700 font-medium mb-2">
                        Category Name
                    </label>
                    <input
                        type="text"
                        id="category-name"
                        className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        value={category_name}
                        onChange={handleCategoryName}
                        placeholder="Enter category name"
                        required
                    />
                </div>
                <div className="mb-4">
                    <label htmlFor="category-description" className="block text-gray-700 font-medium mb-2">
                        Description
                    </label>
                    <textarea
                        id="category-description"
                        className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        value={description}
                        onChange={handleCategoryDescription}
                        placeholder="Enter description"
                        rows="4"
                        required
                    ></textarea>
                </div>
                <button
                    type="submit"
                    className="w-full py-2 px-4 bg-blue-500 text-white font-semibold rounded-md hover:bg-blue-600"
                >
                    {isEditing ? "Update" : "Submit"}
                </button>
            </form>

            {/* Category Table */}
            <div className="mt-6">
                <table className="w-full table-auto border-collapse">
                    <thead>
                        <tr className="bg-gray-100 text-gray-700">
                            <th className="py-3 px-4 border-b">Category Name</th>
                            <th className="py-3 px-4 border-b">Description</th>
                            <th className="py-3 px-4 border-b">Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {category.map((cate) => (
                            <tr key={cate.category_id} className="hover:bg-gray-50">
                                <td className="py-3 px-4 border-b">{cate.category_name}</td>
                                <td className="py-3 px-4 border-b">{cate.description}</td>
                                <td className="py-3 px-4 border-b">
                                    <button
                                        type="button"
                                        className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 mr-2"
                                        onClick={() => openEditForm(cate.category_id)}
                                    >
                                        EDIT
                                    </button>
                                    <button
                                        type="button"
                                        className="px-4 py-2 bg-red-500 text-white rounded-md hover:bg-red-600"
                                        onClick={() => deleteCategory(cate.category_id)}
                                    >
                                        DELETE
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </Fragment>
    );
};

export default Categories;
