import React, { Fragment, useEffect, useState } from 'react';

const Categories = () => {
    const [category, setCategory] = useState([]);
    const [category_name, setCategoryName] = useState('');
    const [description, setDescription] = useState('');
    const [editCategory, setEditCategory] = useState(null);
    const [isEditOpen, setIsEditOpen] = useState(false);

    const submitCategory = async (e) => {
        e.preventDefault();
        try {
            const body = { category_name, description };
            const response = await fetch("http://localhost:3000/category", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body),
            });
            if (response.ok) {
                setCategoryName('');
                setDescription('');
                await allCategories();
            }
        } catch (err) {
            console.error(err.message);
        }
    };

    const allCategories = async () => {
        try {
            const response = await fetch("http://localhost:3000/category");
            const responseData = await response.json();
            setCategory(responseData);
        } catch (err) {
            console.error(err.message);
        }
    };

    const deleteCategory = async (id) => {
        try {
            const response = await fetch(`http://localhost:3000/category/${id}`, {
                method: "DELETE",
            });
            if (response.ok) {
                setCategory(category.filter((cate) => cate.category_id !== id));
            }
        } catch (err) {
            console.error("Error:", err.message);
        }
    };

    const updateCategory = async (e) => {
        e.preventDefault();
        try {
            const body = { category_name: editCategory.category_name, description: editCategory.description };
            const response = await fetch(`http://localhost:3000/category/${editCategory.category_id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body),
            });
            if (response.ok) {
                setIsEditOpen(false);
                setEditCategory(null);
                await allCategories();
            }
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        allCategories();
    }, []);

    return (
        <Fragment>
            {/* Category Form */}
            <form onSubmit={submitCategory} className="p-6 bg-white rounded-lg shadow-md">
                {/* Form Fields */}
            </form>

            {/* Edit Modal */}
            {isEditOpen && (
                <div className="fixed inset-0 bg-gray-800 bg-opacity-50 flex justify-center items-center">
                    <div className="bg-white p-6 rounded-lg shadow-lg">
                        {/* Modal Fields */}
                    </div>
                </div>
            )}

            {/* Category Table */}
            <div className="mt-6">
                <table className="w-full table-auto border-collapse">
                    <thead>
                        <tr className="bg-gray-100 text-gray-700">
                            <th>Category Name</th>
                            <th>Description</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {category.map((cate) => (
                            <tr key={cate.category_id}>
                                <td>{cate.category_name}</td>
                                <td>{cate.description}</td>
                                <td>
                                    <button onClick={() => openEditForm(cate)}>EDIT</button>
                                    <button onClick={() => deleteCategory(cate.category_id)}>DELETE</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </Fragment>
    );
};

export default Categories;
